INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34239, 1801, 'Buspirone', 'Liver Diseases', 'Buspirone is primarily metabolized by the liver and subsequently eliminated by the kidney.  In one study, steady-state area under the plasma concentration-time curve (AUC) of buspirone increased 13-fold in subjects with hepatic impairment and 4-fold in those with renal impairment compared to healthy subjects.  Therapy with buspirone is not recommended in the presence of significantly impaired hepatic or renal function.', '3', 'Gammans RE, Mayol RF, Labudde JA "Metabolism and disposition of buspirone." Am J Med 80 (1986):  41-51|Dalhoff K, Poulsen HE, Garred P, et al. "Buspirone pharmacokinetics in patients with cirrhosis." Br J Clin Pharmacol 24 (1987):  547-50|Caccia S, Vigano GL, Mingardi G, et al. "Clinical pharmacokinetics of oral buspirone in patients with impaired renal function." Clin Pharmacokinet 14 (1988):  171-7|"Product Information. Buspar (buspirone)." Bristol-Myers Squibb  (2002):', 'DDInter', 0);
