INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34255, 1801, 'Buspirone', 'Substance-Related Disorders', 'In human and animal studies, buspirone has not shown potential for abuse or diversion, and there is no evidence that it causes tolerance or physical and psychological dependence.  However, since it is a CNS-active drug, the manufacturers recommend that therapy with buspirone be administered cautiously in addiction-prone individuals, such as those with a history of alcohol or substance abuse.  It may be prudent to refrain from dispensing large quantities of medication to these patients.', '2', '"Product Information. Buspar (buspirone)." Bristol-Myers Squibb  (2002):', 'DDInter', 0);
