INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34317, 12548, 'Bupropion', 'Weight Loss', 'The use of bupropion is associated with weight alterations.  Both weight gain and weight loss may occur, although the latter is much more common.  Weight loss greater than 2.27 kg was reported in up to 28% of patients, which may be undesirable in patients suffering from anorexia, malnutrition, or excessive weight loss.  Weight change should be monitored during therapy if bupropion is used in these patients.', '1', '"Product Information. Aplenzin (buPROPion)." Bausch Health US (formerly Valeant Pharmaceuticals)  (2022):|"Product Information. Forfivo XL (buPROPion)." Almatica Pharma Inc  (2019):|"Product Information. Wellbutrin SR (buPROPion)." GlaxoSmithKline  (2020):|"Product Information. Wellbutrin (buPROPion)." GlaxoSmithKline  (2020):|"Product Information. Wellbutrin XL (buPROPion)." Bausch Health US (formerly Valeant Pharmaceuticals)  (2022):', 'DDInter', 0);
