INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34748, 1854, 'Calcium carbonate', 'Cardiac Conduction System Disease', 'Calcium is involved in cardiac muscle contraction and electrical impulse conduction.  Therapy with calcium salt formulations (particularly IV) should be administered cautiously to patients with cardiac disease.  Patients receiving cardiac glycosides and concomitant IV calcium may experience arrhythmias.  Therapy with IV calcium should be administered slowly and at reduced dosages in patients with cardiac disease.', '3', '"Product Information. Posture (calcium phosphate, tribasic)." Whitehall-Robbins|"Product Information. Neo-Calglucon (calcium glubionate)." Sandoz Pharmaceuticals Corporation  (2001):', 'DDInter', 0);
