INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34781, 1854, 'Calcium carbonate', 'Sarcoidosis', 'Hypercalciuria, with or without hypercalcemia, may occasionally occur in patients with sarcoidosis.  Elevated calcium levels may result from increased intestinal absorption of calcium, which is related to the extrarenal production of vitamin D by mononuclear phagocytes present within the sarcoid granuloma.  Therapy with calcium salts should be administered cautiously and only if necessary in patients with sarcoidosis.', '3', '"Product Information. Posture (calcium phosphate, tribasic)." Whitehall-Robbins|"Product Information. Neo-Calglucon (calcium glubionate)." Sandoz Pharmaceuticals Corporation  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):', 'DDInter', 0);
