INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34792, 13907, 'Adalimumab', 'Infections', 'There have been reports of serious infections, including sepsis and opportunistic infections due to bacterial, mycobacterial, invasive fungal, viral, parasitic, or other opportunistic pathogens including aspergillosis, blastomycosis, candidiasis, coccidioidomycosis, histoplasmosis, legionellosis, listeriosis, pneumocystosis and tuberculosis with TNF blockers.  Treatment with these agents should not be initiated in patients with an active infection, including clinically important localized infections.  Caution and close monitoring is recommended when considering their use in patients with a history of recurrent infections, underlying conditions which may predispose them to infections, or chronic, latent, or localized infections.  Administration of these agents should be discontinued if a patient develops a serious infection or sepsis.', '3', '"Product Information. Remicade (infliximab)." Centocor Inc  (2001):|"Product Information. Humira (adalimumab)." Abbott Pharmaceutical  (2003):|"Product Information. Cimzia (certolizumab)." UCB Pharma Inc  (2008):|"Product Information. Simponi (golimumab)." Centocor Inc  (2009):', 'DDInter', 0);
