INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34822, 13907, 'Adalimumab', 'Hematologic Diseases', 'Cases of leukopenia, neutropenia, thrombocytopenia, and pancytopenia, some with a fatal outcome, have been reported in patients treated with TNF blocking agents.  Caution should be exercised when treating patients who have ongoing or a history of significant hematologic abnormalities.  Discontinuation of therapy should be considered in patients with confirmed significant hematologic abnormalities.', '2', '"Product Information. Remicade (infliximab)." Centocor Inc  (2001):|"Product Information. Humira (adalimumab)." Abbott Pharmaceutical  (2003):|"Product Information. Cimzia (certolizumab)." UCB Pharma Inc  (2008):|"Product Information. Simponi (golimumab)." Centocor Inc  (2009):', 'DDInter', 0);
