INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17070, 2934, 'Pirfenidone', 'Kidney Diseases', 'Pirfenidone should be used with caution in patients with mild, moderate, or severe renal impairment, monitoring for adverse reactions and considering dosage modifications or discontinuation as needed.  The safety and efficacy of pirfenidone has not been studied in patients with end-stage renal disease requiring dialysis, so its use is not recommended in these patients.', '2', '"Product Information. Esbriet (pirfenidone)." Intermune Inc  (2013):', 'DDInter', 0);
