INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17705, 2503, 'Potassium chloride', 'Acidosis', 'Hypokalemia in patients with metabolic acidosis should be treated with an alkalinizing potassium salt (i.e. acetate, bicarbonate, citrate, or gluconate) rather than potassium chloride, since alkali therapy helps to promote cellular uptake of potassium.  Close monitoring of acid-base balance, serum electrolytes, electrocardiogram, and clinical status is recommended.', '2', 'Leiter LA, Josse RG, West ML, Halperin ML "Severe metabolic acidosis induced in a patient during fasting by KCl administration." Clin Invest Med 11 (1988):  266-70|"Product Information. K-Dur (potassium chloride)." Schering Corporation  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):', 'DDInter', 0);
