INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17794, 6705, 'Potassium citrate', 'Alkalosis', 'Hypokalemia in patients with metabolic or respiratory alkalosis should generally be treated with potassium chloride rather than an alkalinizing potassium salt (i.e. acetate, bicarbonate, citrate, or gluconate), since alkali therapy may exacerbate the condition.  In addition, hypochloremia may accompany alkalosis, which is best treated with potassium chloride.  Close monitoring of acid-base balance, serum electrolytes, electrocardiogram, and clinical status is recommended.', '2', 'Walker WG, Jost LJ "Relative roles of patassium and chloride in correction of hypokalemic hypochloremic alkalosis." Johns Hopkins Med J 120 (1967):  148-54|"Product Information. Potassium Acetate (potassium acetate)." Abbott Pharmaceutical  (2002):|"Product Information. K-Lyte (potassium bicarbonate-potassium citrate)." Bristol-Myers Squibb  (2002):|"Product Information. Kaon (potassium gluconate)." Savage Laboratories  (2002):', 'DDInter', 0);
