INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17803, 0, 'Pralidoxime', 'Myasthenia Gravis', 'Pralidoxime reactivates cholinesterase activity resulting in reduced levels of  acetylcholine and increased neuromuscular junction activity.  Treatment of organophosphate poisoning in patients myasthenia gravis with pralidoxime can precipitate a myasthenic crisis.', '3', '"Product Information. Protopam Chloride (pralidoxime)." Baxter Healthcare Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17804, 0, 'Pralidoxime', 'Kidney Diseases', 'Pralidoxime is eliminated by the kidney.  Approximately 80% to 90% of pralidoxime is excreted in the urine as unchanged drug.  The serum concentration of pralidoxime is increased in patients with renal impairment.  Therapy with pralidoxime should be administered cautiously and dosages reduced in patients with compromised renal function.  Clinical monitoring of renal function and muscular activity is recommended.', '2', '"Product Information. Protopam Chloride (pralidoxime)." Baxter Healthcare Corporation  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17805, 3367, 'Potassium Iodide', 'Hyperthyroidism', 'The use of iodide salts has been associated with the exacerbation or precipitation of hyperthyroidism in patients previously euthyroid.  Induction of goiter has been reported in patients with hyperthyroidism.  Iodide salts should be administered cautiously in patients with hyperthyroidism.', '2', 'Iancu T, Boyanower Y, Laurian N "Congenital goiter due to maternal ingestion of iodide." Am J Dis Child 128 (1974):  528-30|Ayromlooi J "Congenital goiter due to maternal ingestion of iodides." Obstet Gynecol 39 (1972):  818-22|Denham MJ, Himsworth RL "Hyperthyroidism induced by potassium iodide given in the course of 125I-fibrinogen test." Age Ageing 3 (1974):  221-5|Yalow RS "Risks in mass distribution of potassium iodide." Bull N Y Acad Med 59 (1983):  1020-7|"Product Information. SSKI (saturated) (potassium iodide)." Upsher-Smith Laboratories Inc|"Product Information. Pima (potassium iodide)." Fleming and Company  (2022):|Carswell F, Kerr MM, Hutchison JH "Congenital goitre and hypothyroidism produced by maternal ingestion of iodides." Lancet 1 (1970):  1241-3|Parmelle A, et al. "Three cases of newborn infants with congential goiter due to ingestion of iodide." Am J Obstet Gynecol 40 (1940):  145-7', 'DDInter', 0);
