INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17861, 0, 'Plecanatide', 'Dehydration', 'Plecanatide should be used with caution in dehydrated patients and patients with severe diarrhea.  Plecanatide is contraindicated in patients less than 6 years of age as these patients may be more likely to develop severe diarrhea and its potentially serious consequences.  Care is recommended when using this agent in adult patients with electrolyte abnormalities or with severe diarrhea.  It is recommended to suspend dosing and rehydrate the patient if such symptoms occur during therapy.', '3', '"Product Information. Trulance (plecanatide)." Synergy Pharmaceuticals Inc.  (2017):', 'DDInter', 0);
