INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17877, 2559, 'Acetazolamide', 'Acidosis, Respiratory', 'Carbonic anhydrase inhibitors may exacerbate pulmonary disease in patients with elevated pCO2 levels.  Respiratory acidosis may be precipitated or increased in these patients.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with respiratory acidosis, and conditions where alveolar ventilation may be impaired (pulmonary obstruction, emphysema, etc) and can precipitate or aggravate acidosis.  Respiratory status should be monitored during therapy.', '2', 'Watson WA, Garrelts JC, Zinn PD, Garriott JC, McLemore TL, Clementi WA "Chronic acetazolamide intoxication." J Toxicol Clin Toxicol 22 549-63|Siklos P, Henderson RG "Severe acidosis from acetazolamide in a diabetic patient." Curr Med Res Opin 6 (1979):  284-6|"Product Information. Diamox (acetazolamide)." Lederle Laboratories  (2001):|"Product Information. Neptazane (methazolamide)." Wyeth-Ayerst Laboratories  (2006):', 'DDInter', 0);
