INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17928, 13086, 'Pramipexole', 'Psychotic Disorders', 'Ordinarily, patients with major psychotic disorder should not be treated with dopaminergic antiparkinson agents, because of the risk of exacerbating psychosis.  Hallucinations and psychotic-like behavior have been reported with dopaminergic medications.  In addition, certain medications used to treat psychosis may exacerbate the symptoms of Parkinson''s disease and may decrease the effectiveness of these drugs.', '3', '"Product Information. Dostinex (cabergoline)." Pharmacia and Upjohn  (2001):|"Product Information. Mirapex (pramipexole)." Boehringer Ingelheim  (2001):|"Product Information. Requip (ropinirole)." SmithKline Beecham  (2001):|"Product Information. Comtan (entacapone)." Novartis Pharmaceuticals  (2001):|"Product Information. Lodosyn (carbidopa)." DuPont Pharma  (2001):|"Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA  (2006):|"Product Information. Neupro (rotigotine)." Schwarz Pharma  (2007):|"Product Information. Xadago (safinamide)." US WorldMeds LLC  (2017):', 'DDInter', 0);
