INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17983, 929, 'Prilocaine', 'Methemoglobinemia', 'Prilocaine has induced methemoglobinemia resulting in respiratory distress and cyanosis.  Therapy with prilocaine topical should be administered cautiously to patients with or predisposed to methemoglobinemia.  If life-threatening or severe methemoglobinemia develops, methylene blue infused at a dosage of 1 to 2 mg/kg body weight will rapidly reverse or improve the condition, except in G-6-PD-deficient patients.', '3', '"Product Information. Citanest Plain (prilocaine)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
