INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17995, 929, 'Prilocaine', 'Liver Failure', 'Prilocaine topical is absorbed through intact skin and mucosal membranes.  Prolonged exposure, large doses, and/or application to compromised skin or mucosa can result in elevated plasma concentrations of prilocaine.  Prilocaine is metabolized by the liver and to some extent by the kidney. The pharmacokinetic disposition of prilocaine is altered by changes in hepatic and renal function. Therapy with prilocaine topical should be administered cautiously and dosing modified for patients with compromised hepatic and renal function.', '2', '"Product Information. Citanest Plain (prilocaine)." Astra-Zeneca Pharmaceuticals  (2001):', 'DDInter', 0);
