INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38415, 2233, 'Ceftazidime', 'Heart Failure', 'Fortaz, Tazicef, and Tazidime (brands of parenteral ceftazidime pentahydrate) are formulated with sodium carbonate and contain approximately 53 mg (2.3 mEq) of sodium per each gram of ceftazidime activity.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.  Ceptaz, another brand of ceftazidime, is formulated with l-arginine and contains no sodium.', '2', '"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Tazicef (ceftazidime)." SmithKline Beecham  (2002):|"Product Information. Ceptaz (ceftazidime)." Glaxo Wellcome  (2001):|"Product Information. Tazidime (ceftazidime)." Lilly, Eli and Company  (2001):', 'DDInter', 0);
