INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38784, 306, 'Chloroquine', 'Seizures', '4-aminoquinolines may cause epileptic seizures in susceptible individuals.  Patients with epilepsy or an otherwise low seizure threshold may be at greater risk.  Therapy with 4-aminoquinolines, particularly chloroquine, should be administered cautiously in patients with epilepsy.', '2', 'Fish DR, Espir ML "Convulsions associated with prophylactic antimalarial drugs: implications for people with epilepsy." Br Med J 297 (1988):  526-7|"Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals  (2002):|Mulhauser P, Allemann Y "Chloroquine and nonconvulsive status epilepticus." Ann Intern Med 123 (1995):  76-7|"Product Information. Plaquenil (hydroxychloroquine)." Apothecon Inc  (2022):|Benbadis SR, Vanness PC "Chloroquine and nonconvulsive status epilepticus." Ann Intern Med 124 (1996):  614', 'DDInter', 0);
