INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38789, 306, 'Chloroquine', 'Glucosephosphate Dehydrogenase Deficiency', 'Hemolysis and acute renal failure have been reported during use of aminoquinolines in patients with glucose-6-phosphate dehydrogenase (G-6-PD) deficiency.  Therapy with aminoquinolines should be administered cautiously in these patients.  Blood cell counts and hemoglobin determinations should be performed regularly.  Aminoquinoline therapy should be discontinued immediately if signs suggestive of hemolytic anemia occur, such as marked darkening of the urine or sudden decrease in hemoglobin concentration or erythrocytic count.', '2', '"Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals  (2002):|"Product Information. Plaquenil (hydroxychloroquine)." Apothecon Inc  (2022):', 'DDInter', 0);
