INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38799, 306, 'Chloroquine', 'Myasthenia Gravis', 'Certain aminoquinolines such as chloroquine, and hydroxychloroquine should be used with caution in patients with myasthenia gravis (MG).  These agents may exacerbate or trigger new onset of symptoms in patients with previously diagnosed MG.  It is recommended to advise patients with MG about this possible side effect.', '2', '"Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals  (2002):|"Product Information. Plaquenil (hydroxychloroquine)." Apothecon Inc  (2022):', 'DDInter', 0);
