INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39562, 2520, 'Cholestyramine', 'Cholestasis', 'The use of bile acid sequestrants is contraindicated in patients with complete biliary obstruction where bile is not secreted into the intestine.  These agents adsorb and combine with bile acids in the intestine to form an insoluble complex that is excreted in the feces, resulting in partial removal of bile acids from the enterohepatic circulation.  Bile acid sequestrants are ineffective if bile does not reach the intestine.', '3', '"Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc  (2002):', 'DDInter', 0);
