INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39696, 2665, 'Cisplatin', 'Toxic Optic Neuropathy', 'Optic neuritis, papilledema, and cortical blindness have been reported in patients receiving standard recommended doses of cisplatin injection.  Blurred vision and altered color perception have been reported after the use of regimens with higher doses and dose frequencies of cisplatin injection.  Improvement and/or total recovery usually occurs after discontinuing cisplatin injection but can be delayed.  Caution is advised in patients with visual disabilities.', '2', '"Product Information. Platinol (cisplatin)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
