INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40437, 2416, 'Citalopram', 'Kidney Diseases', 'Citalopram (racemic) and escitalopram (the more pharmacologically active S-enantiomer) are extensively metabolized and only small amounts are excreted unchanged in the urine.  No dosage adjustments are necessary in patients with mild to moderate renal impairment, as the oral clearance of citalopram has been shown to decrease only modestly (17%) in these patients compared to those with normal renal function.  There is no information on the pharmacokinetics of citalopram or escitalopram in patients with severe renal impairment (CrCl < 20 mL/min).  Therapy with these agents should be administered cautiously in such patients.', '2', '"Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):|"Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals  (2002):', 'DDInter', 0);
