INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40607, 21946, 'Cladribine', 'Nervous System Diseases', 'Severe unspecified neurological toxicity has been reported rarely during cladribine therapy administered at therapeutic doses.  Serious neurological toxicity such as irreversible paraparesis and quadriparesis has been reported in patients receiving four to nine times the recommended dosage of cladribine for hairy cell leukemia.  Therapy with cladribine should be administered cautiously to patients with or predisposed to neurological impairment.', '3', '"Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):', 'DDInter', 0);
