INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40611, 21946, 'Cladribine', 'Kidney Diseases', 'The effect of renal impairment on the elimination of cladribine has not been assessed in humans.  Renal toxicity such as acidosis, anuria, elevated serum creatinine has been reported with doses four to nine times the recommended dosage of cladribine for hairy cell leukemia.  Therapy with cladribine should be administered cautiously in patient with compromised renal function.  Clinical monitoring of renal function is recommended.', '2', '"Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):', 'DDInter', 0);
