INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40856, 2758, 'Clindamycin', 'Liver Diseases', 'Clindamycin is primarily metabolized by the liver.  The serum concentration of clindamycin may be increased and the half-life prolonged in patients with severely impaired hepatic function.  In addition, jaundice and liver enzyme abnormalities may occur during use of the drug.  Therapy with clindamycin should be administered cautiously in patients with liver disease.  Dosage adjustments may not be necessary when administered every 8 hours.  However, serum concentrations should be monitored during high-dose therapy, and periodic liver function tests should be performed during prolonged therapy.', '2', 'Avant GR, Schenker S, Alford RH "The effect of cirrhosis on the disposition and elimination of clindamycin." Dig Dis 20 (1975):  223-30|Hinthorn DR, Baker LH, Romig DA, et al. "Use of clindamycin in patients with liver disease." Antimicrob Agents Chemother 9 (1976):  498-501|Eng RH, Gorski S, Person A, et al. "Clindamycin elimination in patients with liver disease." J Antimicrob Chemother 8 (1981):  277-81|"Product Information. Cleocin (clindamycin)." Pharmacia and Upjohn  (2002):', 'DDInter', 0);
