INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41000, 720, 'Clomipramine', 'Epilepsy', 'Tricyclic antidepressants (TCAs), can lower the seizure threshold and trigger seizures.  These drugs should be used with extreme caution in patients with a history of seizures, or other predisposing factors, such as head trauma, CNS abnormalities, and alcoholism.  Daily dose restrictions might apply for specific antidepressants.  Physicians are encouraged to get additional dosing recommendations on the manufacturer''s prescribing information.', '3', '"Product Information. Pamelor (nortriptyline)." Sandoz Pharmaceuticals Corporation  (2002):|"Product Information. Elavil (amitriptyline)." Stuart Pharmaceuticals  (2002):|"Product Information. Norpramin (desipramine)." Hoechst Marion Roussel  (2002):|"Product Information. Tofranil (imipramine)." Novartis Pharmaceuticals  (2002):|"Product Information. Anafranil (clomipramine)." Basel Pharmaceuticals  (2001):|"Product Information. Asendin (amoxapine)." Lederle Laboratories  (2001):|"Product Information. Surmontil (trimipramine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Vivactil (protriptyline)." Merck & Co., Inc  (2001):|Settle EC "Antidepressant drugs: disturbing and potentially dangerous adverse effects." J Clin Psychiatry 59 Suppl 16 (1998):  25-30', 'DDInter', 0);