INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41375, 1682, 'Clopidogrel', 'Liver Diseases', 'The pharmacokinetic disposition of clopidogrel in patients with liver disease has not been established.  Clopidogrel undergoes biotransformation to an active metabolite that has not yet been isolated and an inactive form that represents approximately 85% of circulating clopidogrel.  The metabolic and therapeutic activity of clopidogrel may be altered in patients with hepatic impairment and should be administered cautiously in patients with severe hepatic dysfunction.  Clinical monitoring of hepatic function and bleeding activity is recommended.', '2', '"Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
