INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41390, 1682, 'Clopidogrel', 'Kidney Diseases', 'Clopidogrel is partially eliminated by the kidney.  Approximately 50% of clopidogrel is excreted in the urine and 45% in the feces.  Plasma concentrations of the inactive metabolite and the degree of platelet inhibition are reduced in patients with severe renal impairment (creatinine clearance of 5-15 mL/min).  However, bleeding times in patients with severe renal impairment did not differ  from those with normal renal function.  No dosage adjustment is recommended for patients with compromised renal function.', '1', '"Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
