INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41602, 2822, 'Clozapine', 'Kidney Diseases', 'Clozapine is extensively metabolized by the liver and subsequently excreted in the urine (50%) and feces (35%), primarily as metabolites.  One of the metabolites appears to have only limited pharmacologic activity, while the others are inactive.  However, the effects of possible metabolite accumulation have not been studied.  The manufacturer recommends that therapy with clozapine be administered cautiously in patients with impaired renal function.', '2', 'Jann MW, Grimsley SR, Gray EC, Chang WH "Pharmacokinetics and pharmacodynamics of clozapine." Clin Pharmacokinet 24 (1993):  161-76|"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
