INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41781, 14117, 'Colesevelam', 'Gastrointestinal Diseases', 'The manufacturer considers colesevelam contraindicated in patients with a history of bowel obstruction.  Because of its constipating effects, colesevelam is not recommended in patients with gastroparesis, other gastrointestinal motility disorders, and in those who have had major gastrointestinal tract surgery and who may be at risk for bowel obstruction.  Because of the tablet size, colesevelam tablets can cause dysphagia or esophageal obstruction and should be used with caution in patients with dysphagia or swallowing disorders.', '3', '"Product Information. Welchol (colesevelam)." Daiichi Sankyo, Inc.  (2001):', 'DDInter', 0);
