INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41790, 14117, 'Colesevelam', 'Malabsorption Syndromes', 'Bile acid sequestrants, including colesevelam may decrease the absorption of fat-soluble vitamins A, D, E, and K.  Caution should be exercised when treating patients with a susceptibility to deficiencies of vitamin K (e.g., patients on warfarin, patients with malabsorption syndromes) or other fat-soluble vitamins.  The manufacturer recommends to instruct patients taking colesevelam and oral vitamin supplementation to take their vitamins at least 4 hours prior to colesevelam.', '2', '"Product Information. Welchol (colesevelam)." Daiichi Sankyo, Inc.  (2001):', 'DDInter', 0);
