INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41813, 13724, 'Alfuzosin', 'Cataract', 'Intraoperative Floppy Iris Syndrome has been observed during cataract surgery in some patients on or previously treated with antiadrenergic agents.  If a patient is undergoing cataract surgery, the ophthalmologist should be prepared for possible modifications of the surgical technique (iris hooks, iris dilator rings).  There does not appear to be a benefit in stopping antiadrenergic agents therapy prior to the cataracts surgery.', '2', '"Product Information. Uroxatral (alfuzosin)." sanofi-aventis  (2003):|"Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals  (2008):', 'DDInter', 0);
