INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41842, 0, 'Conjugated estrogens', 'Angioedema', 'The use of exogenous estrogens may induce or exacerbate symptoms of angioedema, particularly in women with hereditary angioedema.  Close monitoring is recommended when prescribing these agents to patients predisposed to angioedema.', '2', '"Product Information. Ortho-Novum 10/11 (ethinyl estradiol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho-Cept (desogestrel-ethinyl estradiol)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Ortho-Cyclen (ethinyl estradiol-norgestimate)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Lo/Ovral (ethinyl estradiol-norgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Ortho-Novum 1/50 (mestranol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho Dienestrol (dienestrol topical)." Ortho McNeil Pharmaceutical|"Product Information. Nextstellis (drospirenone-estetrol)." Mayne Pharma  (2021):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41843, 0, 'Conjugated estrogens', 'Gallbladder Diseases', 'A two- to four-fold increase in risk of gallbladder disease has been noted in women receiving postmenopausal estrogen therapy.  The risk for gallbladder disease may be less for premenopausal women using oral contraceptives containing low-dose estrogens and/or progestins.  Therapy with estrogens should be administered cautiously in patients with preexisting gallbladder disease.', '2', 'Boston Collaborative Drug Surveilance Program "Surgically confirmed gallbladder disease, venous thromboembolism, and breast tumors in relation to postmenopausal estrogen therapy." N Engl J Med 290 (1974):  15-9|"Product Information. Premarin (conjugated estrogens)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Climara (estradiol)." Berlex Laboratories  (2001):|"Product Information. Estrace (estradiol)." Warner Chilcott Laboratories  (2001):|"Product Information. Estraderm (estradiol)." Ciba-Geigy Pharmaceuticals  (2001):|"Product Information. Vivelle (estradiol)." Ciba-Geigy Pharmaceuticals  (2001):|"Product Information. Ortho-Novum 10/11 (ethinyl estradiol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho-Cept (desogestrel-ethinyl estradiol)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Demulen 1/50 (ethinyl estradiol-ethynodiol)." Searle|"Product Information. Triphasil (ethinyl estradiol-levonorgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Ortho-Cyclen (ethinyl estradiol-norgestimate)." Ortho McNeil Pharmaceutical  (2001):|"Product Information. Lo/Ovral (ethinyl estradiol-norgestrel)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Ortho-Novum 1/50 (mestranol-norethindrone)." Ortho McNeil Pharmaceutical|"Product Information. Ortho-Est (estropipate)." Ortho McNeil Pharmaceutical  (2001):|"Produ