INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41997, 14350, 'Cytarabine', 'Liver Diseases', 'Cytarabine is extensively metabolized by the liver.  Patients with impaired hepatic function may be at increased risk for CNS toxicity during high dose cytarabine therapy.  Therapy with cytarabine should be administered cautiously and the dosages modified in patients with or predisposed to compromised hepatic function.  Periodic checks of liver function should be performed in patients receiving Cytarabine Injection.', '2', '"Product Information. Cytosar-U (cytarabine)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
