INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42288, 4352, 'Darolutamide', 'Hepatic Insufficiency', 'Patients with moderate hepatic impairment (Child-Pugh Class B) have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild hepatic impairment.  The effect of severe hepatic impairment (Child-Pugh C) on darolutamide pharmacokinetics is unknown.', '2', '"Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc.  (2019):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42289, 4352, 'Darolutamide', 'Kidney Diseases', 'Patients with severe renal impairment (eGFR 15-29 mL/min/1.73 m2) who are not receiving hemodialysis have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild or moderate renal impairment.  The effect of end stage renal disease (eGFR <15 mL/min/1.73 m2) on darolutamide pharmacokinetics is unknown.', '2', '"Product Information. Adakveo (crizanlizumab)." Novartis Pharmaceuticals  (2019):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42290, 3326, 'Dapsone', 'Bone Marrow Failure Disorders', 'The use of dapsone has been associated with development of agranulocytosis, aplastic anemia, and other blood dyscrasias.  Blood dyscrasias are more likely to occur in patients with glucose-6-phosphate dehydrogenase (G-6-PD) deficiency.  Patients should be instructed to report any signs of infection (sore throat, fever, local infection, bleeding, or symptoms of anemia).  Monitoring for hematologic abnormalities is recommended.', '3', 'Laing A "Unusual reaction to intramuscular sulfone in two leprosy patients in malaysia." Int J Lepr 48 (1980):  70|McConkey B "Adverse reactions to dapsone." Lancet 2 (1981):  525|Cook I, Kish M "Haematological safety of long-term malarial prophylaxis with dapsone-pyrimethamine." Med J Aust 143 (1985):  139-41|Potter M, Yates P, Slade R, Kennedy C "Agranulocytosis caused by dapsone therapy for granuloma annulare." J Am Acad Dermatol 20 (1989):  87-8|Hornsten P, Keisu M, Wiholm B "The incidence of agranulocytosis during treatment of dermatitis herpetiformis with dapsone as reported in Sweden, 1972 through 1988." Arch Dermatol 126 (1990):  919-22|Duhra P, Charles-Holmes R "Linear IgA disease with haemorrhagic pompholyx and dapsone-induced neutropenia." Br J Dermatol 125 (1991):  172-4|Kraus A, Jakez J, Palacios A "Dapsone induced sulfone syndrome and systemic lupus exacerbation." J Rheumatol 19 (1992):  178-9|"Product Information. Dapsone (dapsone)." Jacobus Pharmaceutical Company  (2022):|Cockburn EM, Wood SM, Waller PC, Bleehen SS "Dapsone-induced agranulocytosis: spontaneous reporting data." Br J Dermatol 128 (1993):  702-3|Woodbury GR, Jr  Fried W, Ertle JO, Malkinson FD "Dapsone-associated agranulocytosis and severe anemia in a patient with leukocytoclastic vasculitis." J Am Acad Dermatol 28 (1993):  781-3|Meyerson MA, Cohen