INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42464, 3511, 'Deferoxamine', 'Auditory Diseases, Central', 'Neurotoxicity-related auditory abnormalities such as tinnitus and hearing loss including deafness have occurred after prolonged therapy or large doses of deferoxamine, or in patients with low ferritin levels.  Therapy with deferoxamine should be administered cautiously in patients with hearing impairment.  Hearing toxicity is usually reversible if detected early and deferoxamine discontinued.', '2', '"Product Information. Desferal (deferoxamine)." Novartis Pharmaceuticals  (2001):', 'DDInter', 0);
