INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42475, 14130, 'Deferasirox', 'Bone Marrow Failure Disorders', 'The use of deferasirox is contraindicated in patients with platelet counts below 50 x 109/L.  Cytopenias, worsening anemia, and thrombocytopenia, including fatal events, have been reported in patients treated with deferasirox.  Preexisting hematologic disorders may increase this risk.  It is recommended to monitor blood counts in all patients and to interrupt treatment in patients who develop cytopenias until the cause of the cytopenia has been determined.', '3', '"Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals  (2005):', 'DDInter', 0);
