INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42508, 14130, 'Deferasirox', 'Kidney Diseases', 'The use of deferasirox is contraindicated in patients with serum creatinine greater than two times the age-appropriate upper limit of normal or creatinine clearance less than 40 mL/min.  The starting dose of deferasirox should be reduced by 50% in patients with creatine clearance 40 to 60 mL/min.  It is recommended to measure serum creatine and calculate clearance in duplicate prior to initiating treatment in all patients and monitor renal function at least monthly or more frequently as clinically appropriate.  Consider dose reduction, interruption, or discontinuation of deferasirox based on increases in serum creatinine.', '3', '"Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals  (2005):', 'DDInter', 0);
