INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42725, 2971, 'Dexmethylphenidate', 'Mental Disorders', 'The use of central nervous system (CNS) stimulants is contraindicated in patients with marked agitation and/or anxiety, since these symptoms may be aggravated.  CNS stimulants may also exacerbate symptoms of behavior disturbance and thought disorder in psychotic patients, particularly children.  Therapy with CNS stimulants should be administered cautiously in patients with a history of psychosis or a predisposition to agitated states.', '3', '"Product Information. Fastin (phentermine)." SmithKline Beecham  (2001):|"Product Information. Cylert (pemoline)." Abbott Pharmaceutical  (2001):|"Product Information. Ritalin (methylphenidate)." Novartis Pharmaceuticals  (2001):|"Product Information. Desoxyn (methamphetamine)." Abbott Pharmaceutical  (2001):|"Product Information. Dexedrine (dextroamphetamine)." SmithKline Beecham  (2001):|"Product Information. Adderall (amphetamine-dextroamphetamine)." Shire Richwood Pharmaceutical Company Inc  (2001):|"Product Information. Didrex (benzphetamine)." Pharmacia and Upjohn  (2001):|"Product Information. Prelu-2 (phendimetrazine)." Boehringer-Ingelheim  (2001):|"Product Information. Tenuate (diethylpropion)." Aventis Pharmaceuticals  (2001):|"Product Information. Sanorex (mazindol)." Novartis Pharmaceuticals  (2001):|"Product Information. Focalin (dexmethylphenidate)." Mikart Inc  (2001):|"Product Information. Vyvanse (lisdexamfetamine)." Shire US Inc  (2007):', 'DDInter', 0);
