INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42785, 6700, 'Desmopressin', 'Thromboembolism', 'Rare cases of thrombotic events (thrombosis, acute cerebrovascular thrombosis, and acute myocardial infarction) have been reported following desmopressin injection in patients predisposed to thrombus formation.  Although a causal relationship has not been established, therapy with desmopressin should be administered cautiously in patients with a current or past history of thrombotic or thromboembolic disorders.', '2', '"Product Information. DDAVP (desmopressin)." Rhone Poulenc Rorer  (2001):|"Product Information. Stimate (desmopressin)." Forest Pharmaceuticals  (2001):|Vermylen J, Peerlinck K "Fatal complication of desmopressin." Lancet 346 (1995):  447', 'DDInter', 0);
