INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21661, 0, 'Rasburicase', 'Glucosephosphate Dehydrogenase Deficiency', 'The antihyperuricemic agents pegloticase and rasburicase are contraindicated in patients deficient in glucose-6-phosphate-dehydrogenase (G6PD), as these have shown to cause severe hemolytic reactions and methemoglobinemia.  Patients at high risk of G6PD deficiency such as patients of African, Mediterranean or Southern Asian ancestry, should be screened prior to starting treatment.  Treatment with any of these drugs should be discontinued immediately and permanently in any patient developing hemolysis or methemoglobinemia.', '3', '"Product Information. Elitek (rasburicase)." Sanofi Winthrop Pharmaceuticals  (2002):|"Product Information. Krystexxa (pegloticase)." Savient Pharmaceuticals  (2010):', 'DDInter', 0);
