INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22567, 0, 'Riociguat', 'Hypotension', 'Riociguat reduces blood pressure.  There is a potential for symptomatic hypotension or ischemia in patients with hypovolemia, severe left ventricular outflow obstruction, resting hypotension, autonomic dysfunction, or concomitant treatment with antihypertensive agents.  Caution is advised when using riociguat in these patients.', '2', '"Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22568, 0, 'Riociguat', 'Liver Failure', 'The safety and efficacy of riociguat have not been demonstrated in patients with severe hepatic impairment (Child-Pugh C).  Care should be exercised when using riociguat in these patients.', '2', '"Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22569, 0, 'Riociguat', 'Kidney Diseases', 'The safety and efficacy of riociguat have not been demonstrated in patients with creatinine clearance < 15 mL/min or on dialysis.  Care should be exercised when using riociguat in these patients.', '2', '"Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc  (2013):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22570, 839, 'Risperidone', 'Dementia', 'Older patients with dementia-related psychosis treated with antipsychotic drugs are at an increased risk of death; although the causes were varied, most of the deaths appeared to be either cardiovascular (e.g., heart failure, sudden death) or infectious (e.g., pneumonia) in nature.  A causal relationship with antipsychotic use has not been established.  In controlled trials in older patients with dementia-related psychosis, patients randomized to risperidone, aripiprazole, and olanzapine had higher incidence of cerebrovascular adverse events (e.g., stroke, transient ischemic attack), including fatalities, compared to patients treated with placebo.  These agents are not approved for the treatment of patients with dementia-related psychosis.', '3', '"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|"Product Information. Risperdal (risperidone)." Janssen Pharmaceuticals  (2001):|"Product Information. Zyprexa (olanzapine)." Lilly, Eli and Company  (2001):|"Product Information. Seroquel (quetiapine)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Geodon (ziprasidone)." Pfizer U.S. Pharmaceuticals  (2001):|"Product Information. Abilify (aripiprazole)." Bristol-Myers Squibb  (2002):|"Product Information. Invega (paliperidone)." Janssen Pharmaceuticals  (2007):|"Product Information. Fanapt (iloperidone)." Vanda Pharmaceuticals Inc  (2009):|"Product Information. Saphris (asenapine)." Schering-Plough Corporation  (2009):|"Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc  (2010):|"Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc  (2015):|"Product Information. Vraylar (cariprazine)." Actavis Pharma, Inc.  (2015):|"Product Information. Nuplazid (pimavanserin)." Accelis Pharma  (2016):|"Prod