INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23756, 0, 'Rolapitant', 'Hepatic Insufficiency', 'No dosage adjustment is necessary when using rolapitant in patients with mild or moderate hepatic impairment.  The use of rolapitant should be avoided in patients with severe hepatic impairment as there are no clinical or pharmacokinetic data available for these patients.  Close monitoring is recommended.', '3', '"Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23757, 0, 'Rolapitant', 'Lactose Intolerance', 'Rolapitant tablets contain lactose.  Care should be exercised when using this agent in patients with rare hereditary problems of galactose intolerance, severe lactase deficiency with a severe degree of intolerance to lactose- containing products, or of glucose-galactose malabsorption.', '2', '"Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23758, 0, 'Rolapitant', 'Kidney Diseases', 'Population pharmacokinetic analyses suggest that no dose adjustment of rolapitant is required in patients with mild or moderate renal impairment.  The effect of severe renal impairment on the pharmacokinetics of rolapitant is unknown to determine a dose.  This agent has not been studied in patients with end-stage renal disease requiring hemodialysis.  Caution is recommended.', '2', '"Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23759, 23825, 'Romiplostim', 'Leukemia', 'Progression from myelodysplastic syndromes (MDS) to acute myelogenous leukemia (AML) has been observed in clinical trials with romiplostim.  This drug is not indicated for the treatment of thrombocytopenia due to MDS or any other cause other than chronic ITP.', '3', '"Product Information. Nplate (romiplostim)." Amgen USA  (2008):', 'DDInter', 0);
