INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24210, 0, 'Sacituzumab govitecan', 'Hepatic Insufficiency', 'The safety of sacituzumab govitecan has not been established in patients with moderate or severe liver dysfunction; it has not been tested in patients with serum bilirubin greater than 1.5 times the upper limit of normal (1.5 x ULN), or AST and ALT greater than 3 x ULN, or AST and ALT greater than 5 x ULN and associated with liver metastases.  Because the tolerability of sacituzumab govitecan was not investigated in these patients, no recommendations can be made for the starting dose; caution is advised in patients with moderate or severe liver dysfunction.  No adjustment to the starting dose is needed in patients with mild liver dysfunction (bilirubin up to 1.5 x ULN and AST/ALT less than 3 x ULN).', '2', '"Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics  (2020):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24211, 0, 'Sacituzumab govitecan', 'Kidney Diseases', 'There are no data on the pharmacokinetics of sacituzumab govitecan in patients with moderate renal dysfunction or end-stage renal disease (CrCl up to 30 mL/min); caution is recommended in these patients.', '2', '"Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics  (2020):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24212, 15196, 'Safinamide', 'Hypotension', 'Dopamine agonists may impair the systemic regulation of blood pressure, with resultant orthostatic hypotension, especially during dose escalation.  Therapy with dopamine agonists should be monitored carefully in patients with Parkinson''s disease since they may have an impaired ability to respond to an orthostatic challenge, and also in patients receiving antihypertensive drugs.', '3', 'Hoehn MM "Levodopa-induced postural hypotension. Treatment with  fludrocortisone." Arch Neurol 32 (1975):  50-1|Iwasaki S, Hamaguchi K, Iwasaki A, Takakusagi M, Narabayashi Y "Hypotensive effect of long-term levodopa in patients with Parkinson''s  disease." Eur Neurol 30 (1990):  194-9|"Product Information. Dostinex (cabergoline)." Pharmacia and Upjohn  (2001):|"Product Information. Sinemet (carbidopa-levodopa)." DuPont Pharmaceuticals  (2001):|"Product Information. Sinemet CR (carbidopa-levodopa)." DuPont Pharmaceuticals  (2001):|"Product Information. Mirapex (pramipexole)." Boehringer Ingelheim  (2001):|"Product Information. Requip (ropinirole)." SmithKline Beecham  (2001):|"Product Information. Comtan (entacapone)." Novartis Pharmaceuticals  (2001):|"Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA  (2006):|"Product Information. Neupro (rotigotine)." Schwarz Pharma  (2007):|"Product Information. Xadago (safinamide)." US WorldMeds LLC  (2017):', 'DDInter', 0);
