INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24498, 21497, 'Sapropterin', 'Nervous System Diseases', 'A 10-year sapropterin postmarketing-marketing safety surveillance program for a non-phenylketonuria indication, showed that a few patients with underlying neurological disorders experienced seizures, exacerbation of seizures, over-stimulation, and irritability especially when coadministered with levodopa.  Patients with neurological disorders, especially those receiving levodopa should be monitored for changes in neurological status during treatment with sapropterin.', '2', '"Product Information. Kuvan (sapropterin)." BioMarin Pharmaceutical Inc  (2007):', 'DDInter', 0);
