INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48244, 17912, 'Dronedarone', 'Liver Diseases', 'Dronedarone is extensively metabolized by the liver.  There is little clinical experience with moderate hepatic impairment and none in patients with severe hepatic impairment.  No dosage adjustment is recommended for patients with moderate hepatic impairment, however its use is contraindicated in patients with severe hepatic impairment.  Additionally hepatocellular liver injury, including acute liver failure has been reported in patients using dronedarone in the postmarketing setting.  Patients should be advised to report immediately any symptoms suggesting hepatic injury, such as anorexia, nausea, vomiting, fever, malaise, fatigue, right upper quadrant pain, jaundice, dark urine or itching.', '3', '"Product Information. Multaq (dronedarone)." sanofi-aventis  (2009):', 'DDInter', 0);
