INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48533, 28586, 'Dulaglutide', 'Pancreatitis', 'The use of GLP-1 receptor agonists has been associated with rare reports of acute pancreatitis.  Other antidiabetic therapies should be considered in patients with a previous history of pancreatitis, although it is not known if they are at increased risk.  Patients receiving treatment with GLP-1 receptor agonists should be observed carefully for signs and symptoms of pancreatitis.  If this diagnosis is suspected, the treatment should be discontinued immediately and appropriate management should be initiated.  Treatment with GLP-1 receptor agonists should be permanently discontinued if pancreatitis is confirmed.', '3', '"Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc  (2005):|"Product Information. Tanzeum (albiglutide)." GlaxoSmithKline  (2014):|"Product Information. Trulicity (dulaglutide)." Eli Lilly and Company  (2014):|"Product Information. Saxenda (liraglutide)." Novo Nordisk Pharmaceuticals Inc  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48534, 28587, 'Dulaglutide', 'Pancreatitis', 'The use of GLP-1 receptor agonists has been associated with rare reports of acute pancreatitis.  Other antidiabetic therapies should be considered in patients with a previous history of pancreatitis, although it is not known if they are at increased risk.  Patients receiving treatment with GLP-1 receptor agonists should be observed carefully for signs and symptoms of pancreatitis.  If this diagnosis is suspected, the treatment should be discontinued immediately and appropriate management should be initiated.  Treatment with GLP-1 receptor agonists should be permanently discontinued if pancreatitis is confirmed.', '3', '"Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc  (2005):|"Product Information. Tanzeum (albiglutide)." GlaxoSmithKline  (2014):|"Product Information. Trulicity (dulaglutide)." Eli Lilly and Company  (2014):|"Product Information. Saxenda (liraglutide)." Novo Nordisk Pharmaceuticals Inc  (2015):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48535, 28586, 'Dulaglutide', 'Thyroid Neoplasms', 'GLP-1 receptor agonist antidiabetic drugs are contraindicated in patients with a personal or family history of medullary thyroid carcinoma and in patients with Multiple Endocrine Neoplasia syndrome type 2 (MEN2).  Carcinogenicity studies in rodents and limited postmarketing data suggest that GLP-1 inhibitors may cause a dose-related and treatment duration-related increase in risk of thyroid C-cell tumors, although a causal relationship has not been established.', '3', '"Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc  (2005):|"Product Information. Victoza (liraglutide)." Novo Nordisk Pharmaceuticals Inc  (2010):|"Product Information. Tanzeum (albiglutide)." GlaxoSmithKline  (2014):|"Product Information. Trulicity (dulaglutide)." Eli Lilly and Company  (2014):|"Product Information. Saxenda (liraglutide)." Novo Nordisk Pharmaceuticals Inc  (2015):', 'DDInter', 0);
