INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48545, 30670, 'Durvalumab', 'Colitis', 'Immune-mediated colitis has been reported with the use of durvalumab.  Monitor patients for signs and symptoms of diarrhea or colitis.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents followed by corticosteroid taper for severe or life-threatening colitis.  It is recommended to interrupt or to permanently discontinue durvalumab for life-threatening or for recurrent colitis.  Care should be taken when using durvalumab in patients with inflammatory bowel disease.', '2', '"Product Information. Imfinzi (durvalumab)." Astra-Zeneca Pharmaceuticals  (2017):', 'DDInter', 0);
