INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48567, 29123, 'Dyphylline', 'Cardiotoxicity', 'Like other methylxanthines, dyphylline at high dosages may be associated with positive inotropic and chronotropic effects on the heart.  Therapy with dyphylline and products containing dyphylline should be administered cautiously in patients with severe cardiac disease, hypertension, hyperthyroidism, or recent myocardial infarction.  The relationship between plasma dyphylline levels and therapeutic as well as toxic effects has not been determined.', '2', '"Multum Information Services, Inc. Expert Review Panel"|"Product Information. Lufyllin (dyphylline)." Wallace Laboratories  (2001):', 'DDInter', 0);
